INSERT INTO `verzeichnis` (`id`, `uid`, `name`, `romaji`, `image`, `voiceactorger`, `voiceactorjap`, `rasse`, `birthday`, `herkunft`, `geschlecht`, `groesse`, `family`, `anime`, `manga`, `description`, `mainpage`, `kopfgeld`, `alter`, `piratenbande`, `teufelsfrucht`, `position`, `creator`, `last_editor`) VALUES
(1, '63d5b11225a6c1.20252794', 'Gol D. Roger', 'ゴル D. ロジャー', 'roger', 'Hartmut Neugebauer (†), Michael Brennicke, Holger Schwiers, Patrick Roche', 'Chikao Ohtsuka (†), Masane Tsukayama, Takeshi Kusao ', 'Mensch', '31. Dezember', 'Loguetown, Eastblue', 'Männlich', '2,74m', 'Frau: [ref=Puma D. Rouge]Puma D. Rouge[/ref] - Sohn: [ref=Puma D. Ace]Puma D. Ace[/ref]', 'Episode 48', 'Band 1, Kapitel 1', 'Er ist der König der Piraten!', 1, '5.564.800.000', '53 (†)', 'Rogers Piratenbande', 'keine', 'Kapitän', 3, 506),
(2, '63d5b11225e933.25375204', 'Alvida mit der Eisenkeule', 'アルビダ', 'verzeichnisalvida', 'Angela Wiederhut', 'Yōko Matsuoka', 'Mensch', '14. März', 'East Blue', 'Weiblich', '1,98', 'Unbekannt', 'Episode 01', 'Band 1, Kapitel 2', '[b]Alvida mit der Eisenkeule[/b] ist der erste Gegner von Monkey D. Ruffy, sie trifft auf ihn bereits in der ersten Folge, zu diesem Zeitpunkt ist sie noch sehr dick.\r\n\r\nSie ist eine Piratin aus dem East Blue und aufgrund ihres hohem Kopfgeld sehr gefürchtet.\r\n\r\nIm späteren Verlauf isst Alvida von der Alabasta Frucht (Sube Sube no Mi), diese ist eine Paramecia mit der Fähigkeit welche den Anwender wunderschön und schlank macht, mit Hilfe dieser Teufelsfrucht wird der Körper so glatt dass alle Attacken vom Körper abgleiten und es sehr schwer ist den Gegner zu treffem.\r\n\r\nAlvida nach dem Verzehr der Teufelsfrucht.\r\n\r\n[img]https://opwx.de/img/verzeichnis/verzeichnisalvidaschlank.png[/img]\r\n\r\n\r\n', 0, '5.000.000 ', '27', 'Alvidas Piratenbande', 'keine', 'Kapitän', 3, 3),
(3, '63d5b112266919.27446805', 'Corby', 'コビー', 'verzeichniscorby', 'Katrin Fröhlich (bis Staffel 6), Inez Günther (Staffel 7), Tim Schwarzmaier (Staffel 8)', 'Mika Doi', 'Mensch', '13. Mai', 'East Blue', 'Männlich', '1,67', 'Unbekannt', 'Episode 1', 'Band 01, Kapitel 01', '[b]Corby[/b] ist ein sehr entschlossener junger Mann welcher sich schon früh dafür entscheidet zu der Marine zu gehen um dort Karriere zu machen.\r\n\r\nEr trifft [ref=Monkey D. Ruffy]Ruffy[/ref] das erste mal auf dem Schiff von [ref=Alvida mit der Eisenkeule]Alvida[/ref] und sieht das [b]Corby[/b] ein gefangener ist und wird von ihm befreit, seitdem sind die beiden sehr gute Freunde und das obwohl die beiden wichtige Punkte sehr stark von einander unterscheiden! \r\n\r\n[b]Corby[/b] hat sich in den Jahren sehr weiter entwickelt, auf Water Seven setzt er gegen [ref=Monkey D. Ruffy]Ruffy[/ref] die Technik Rasur, dies ist eine Technik der Rokushiki welche den Anwender mit einer gewaltigen Geschwindigkeit und Treffsicherheit den Gegner attackieren kann.', 1, '/', '18', 'Marine', 'keine', 'Kapitän', 3, 3),
(4, '63d5b112276369.58808443', 'Morgan', '斧手のモーガン大佐', 'verzeichniskmorgan', 'Willi Röbke', 'Banjō Ginga', 'Mensch', '13. April', 'East Blue', 'Männlich', '2,85', 'Sohn: [ref=Helmeppo]Helmeppo[/ref]', 'Episode 02', 'Band 1, Kapitel 4', ' [b]Morgan[/b] war ein gefürchteter Kapitän der Marine Basis auf Shellstown, er wurde später wegen Amtsmissbrauch festgenommen und von seinem Posten enthoben.\r\n\r\nEr ist ebenfalls der Vater von [ref=Helmeppo]Helmeppo[/ref] dem späteren Weggefährten von [ref=Corby]Corby[/ref]\r\n\r\nEr wird auch [b]Morgan mit dem Beilarm[/b] genannt, er hat an seinem rechten Arm ein Beil statt einer Hand mit welchem er sehr gut umgehen kann, er kann damit so hart zuschlagen das sich selbst der Boden spaltet.', 0, '/', '44', 'Marine', 'keine', 'Ehemaliger Kapitän', 3, 3),
(5, '63d5b11227a943.91851165', 'Helmeppo', 'ヘルメッポ', 'verzeichnismeppo', 'Oliver Stritzel (bis Staffel 7), Gerd Meyer (ab Staffel 8, Movies)', 'Kouichi Nagano', 'Mensch', '16. Juli', 'East Blue', 'Männlich', '1,79', 'Vater: [ref=Morgan]Kapitän Morgen[/ref]', 'Episode 02', 'Band 1, Kapitel 3', '[b]Helmeppo[/b] ist der Sohn des ehemaligen Marine Kapitän [ref=Morgan]Morgan[/ref]\r\n\r\nAuf Shellstown ist eher ein verwöhntes Einzelkind welcher den Rang seines Vaters ausnutzt um hoch angesehen zu werden, später jedoch entwickelt er sich weiter und wird zu einem guten Kämpfer.\r\n\r\nNach der Festnahme seines Vaters verfolgt er selber eine Karriere bei der Marine und bekleidet [ref=Corby]Corby[/ref]\r\n\r\n[b]Helmeppo[/b] trainiert mit 2 Kukri-Schwertern, wurde jedoch trotzdem von [ref=Zorro]Zorro[/ref] auf Water 7 ohne Probleme besiegt.', 0, '/', '22', 'Marine', 'keine', 'Korvettenkapitän', 3, 3),
(6, '63d5b1122811f6.45188406', 'Richie der Löwe', 'リチー', 'verzeichnisrichie', '/ ', 'Tetsu Inada', 'Tier -> Katze -> Löwe', '23. Juli', 'East Blue', 'Männlich', '/', 'Besitzer: [ref=Moji]Moji[/ref]', 'Episode 6', 'Band 2, Kapitel 12', '[b]Richie[/b] ist der Löwe von [ref=Moji]Moji[/ref] und ebenfalls ein Mitglied in der Piratenbande von [ref=Buggy der Clown]Buggy[/ref].\r\n\r\nEr ist ein sehr großer Löwe welcher mit seinen Pfoten und Gebiss sehr viel Schaden anrichten kann.', 0, '/', '/', 'Buggys Piratenbande', 'keine', '/', 3, 3),
(7, '63d5b112284960.91574278', 'Moji', '猛獣使いのモージ', 'verzeichnisrichie', 'Claus Brockmeyer Axel Scholtz (Episode 752)', 'Shigenori Souya', 'Mensch', '01. Oktober', 'East Blue', 'Männlich', '1,97', 'Haustier: [ref=Richie der Löwe]Richie[/ref]', 'Episode 06', 'Band 2, Kapitel 9', '[b]Moji[/b] ist ein Mitglied der Piratenbande von [ref=Buggy der Clown]Buggy[/ref]\r\n\r\nEr teilt sich den Posten vom Vize zusammen mit [ref=Cabaji]Cabaji[/ref].\r\n\r\nMoji selber besitzt keine besonderen kämpferischen Fähigkeiten, sein großes Talent liegt daran wilde Tiere zu zähmen wie auch den riesigen Löwen [ref=Richie der Löwe]Richie[/ref] welche die Befehle von [b]Moji[/b] befolgt.', 0, '/', '29', 'Buggys Piratenbande', 'keine', 'Vizekapitän', 3, 3),
(8, '63d5b1122895f7.65839484', 'Chopper', 'トニートニー・チョッパー', 'verzeichnischopper', 'Florian Halm (bis Folge 262), Martin Halm (ab Folge 263)', 'Ikue Ōtani (bis Folge 253, ab Folge 264), Kazue Ikura (Episode 254-263)', 'Tier -> Hirsch -> Rentier', '24. Dezember', 'Grandline, Insel Drum', 'Männlich', '0,70', '/', 'Episode 81', 'Band 15, Kapitel 134', '[b]Chopper[/b] ist in seiner normalen Form (Brain Point) ein sehr kleines und eher schwaches wesen, er besetzt auf dem Schiff von [ref=Monkey D. Ruffy]Ruffy[/ref] die Position vom Schiffsarzt und kümmert sich um die Verletzungen der Crew.\r\n\r\nGeboren wurde er selber als normales Rentier welches später von der Mensch-Mensch Frucht (Hito Hito no Mi) des Types Zoan gegessen hat, auf Grund dieser Frucht ist es Chopper möglich sich in eine Rentier/Mensch gestalt zu verwandeln, nicht nur das... Er schafft es auch dank seiner Rumble Balls in viele verschiedene Verwandlungen zu verwandeln.\r\n\r\nDa die Marine denkt er wäre nur das Haustier der Piratenbande liegt das Kopfgeld aktuell bei 100 Berry.', 1, '100 Berry', '17', 'Strohhut Piratenbande', 'keine', 'Arzt', 3, 3),
(9, '63d5b11228e316.18921814', 'Kabaji', '曲芸のカバジ', 'verzeichnniskabaji', 'Ziad Ragheb, Uwe Kosubek (Episode 512)', 'Moriya Endou', 'Mensch', '08. September', 'East Blue', 'Männlich', '2,08', '/', 'Episode 07', 'Band 2, Kapitel 9', '[b]Kabaji[/b] ist Kommandant in der Piratenbande von [ref=Buggy der Clown]Buggy[/ref].\r\n\r\nIm Kampf gegen Zorro auf Orange Town im East Blue geht er sehr geschickt mit  Schwertern um, zusätzlich setzt er Kreisel ein und spuckt Feuer.\r\n\r\nEr kämpft auf einem Einrad mit welchem er hohe Geschwindigkeiten erreichen kann.\r\n\r\n\r\n\r\n', 0, '/', '34', 'Buggys Piratenbande', 'keine', 'Kommandant', 3, 3),
(10, '63d5b112295358.91173172', 'Lorenor Zorro', 'ロロノア・ゾロ', 'verzeichniszorro', 'Philipp Brammer (bis Folge 400), Uwe Thomsen (ab Folge 401)', 'Kazuya Nakai', 'Mensch', '11. November', 'Shimotsuki, Eastblue', 'Männlich', '1,81', '/', 'Episode 01', 'Band 1, Kapitel 1', '[b]Lorenor Zorro[/b], fälschlicherweise auch als Piratenjäger bekannt, ist das erste Mitglied der Strohhut-Bande. Um seinem Traum, der stärkste Schwertkämpfer zu werden, nachzugehen, gibt er niemals einen Kampf auf. Sanji nennt ihn aufgrund seiner grünen Haare gerne Spinatschädel.\r\n\r\nZorro ist in der Bande eine Autoritätsperson und weist die anderen, wenn nötig, zurecht. Er ist in der Lage, allein mit seinem Blick Feinde in Angst zu versetzen. Mit seinen drei Schwertern ist er ein mächtiger Gegner und sollte es mal ernst werden, bindet er sich das dunkelgrüne Kopftuch an seinem Arm um seinen Kopf.', 0, '320.000.000 Berry', '21', 'Strohhut-Bande', 'keine', 'Vize-Kapitän, Schwertkämpfer', 3, 3),
(11, '63d5b112299035.52022643', 'Nico Robin', 'ニコ・ロビン', 'verzeichnisrobin', 'Simone Brahmann', 'Yuriko Yamaguchi, Yūko Kobayashi, Anzu Nagai (als Kind)', 'Mensch', '6. Februar', 'Ohara, Westblue', 'Weiblich', '1,88', 'Familie Nico ', 'Episode 67', 'Band 13, Kapitel 114', 'Die schlanke [b]Nico Robin[/b] ist das drittälteste Mitglied der Strohhut-Bande. Wie auch Nami gehört sie zu denen, die gerne mal ihr Outfit ändern.\r\n\r\nSie ist sehr stoisch und zeigt ungerne ihre Gefühle. Neugierig und furchtlos ist sie dazu, was alles durch ihre Reaktionen in einigen schockierenden Situationen gezeigt wird. \r\n\r\nSie redet nur, wenn etwas Unvorhergesehenes passiert oder sie direkt angesprochen wird, wobei sie es in den wenigen Instanzen, in denen sie den Mund öffnet, nur allzu oft schafft, die Laune aller nach unten zu ziehen. \r\nIhr Traum ist es, das sagenumwobene Rio-Porneglyph zu finden und zu entziffern, damit sie die Wahre Geschichte erfährt.', 1, '130.000.000 Berry', '30', 'Strohhut-Bande', 'keine', 'Archäologin', 3, 3),
(12, '63d5b11229c735.07469734', 'Monkey D. Ruffy', 'モンキー・D・ルフィ', 'verzeichnisruffy', 'Daniel Schlauch', 'Mayumi Tanaka', 'Mensch', '5. Mai', 'Windmühlendorf, Eastblue', 'Männlich', '1,74', 'Familie Monkey D.', 'Episode 01', 'Band 1, Kapitel 1', '[b]Monkey D. Ruffy[/b], aufgrund seines Signaturhutes weitgehend als Strohhut-Ruffy oder Strohhutpirat bekannt, ist der Gründer und Kapitän der nach diesem benannten Strohhut-Bande. Er hat den Traum, das One Piece zu finden, um somit der König der Piraten zu werden. \r\n\r\nHierfür hat er jedoch eine Definition, die nicht ganz mit der anderer Piraten übereinstimmt. Während andere Piraten als Piratenkönig Herrscher der Weltmeere sein wollen, ist das nicht Ruffys Ziel. Durch das One Piece möchte er grenzenlose Freiheit erlangen, was für ihn den König ausmacht. \r\n\r\nAls Träumer, der gerne seine Gedanken spricht, wird er oft nicht ernst genommen, doch das stört ihn nicht weiter. Er möchte ohne Hilfe außerhalb seiner Crew das One Piece ergattern. Sollte das nicht möglich sein, würde er mit der Piraterie aufhören, da er kein Interesse an einem solch \"langweiligen Abenteuer\" hat. Für diesen Traum ist er durchaus bereit, zu sterben. \r\n\r\nFalls das nicht bereits ausreicht, um zu zeigen, wie furchtlos und abenteuerlustig er ist, hat er sich die Narbe unter seinem Auge als Kind selbst zugefügt, nur um sich bei Shanks zu beweisen. Das alles ändert nichts daran, dass er letztlich doch ein sehr einfältiger Mann ist. \r\n\r\nIm Kampf ist er dennoch dazu fähig, Strategien anzuwenden, die man, basierend auf seinem Auftreten, nicht von ihm erwarten würde.  Bei Wegen und Namen verschwindet diese Genialität wieder gänzlich, weswegen er Leute gerne nach Eigenschaften oder Kleidungsstücken benennt, die er sich leicht merken kann. Da er glaubt, dass eine zerschmetternde Niederlage dem Tod sehr nahe kommt, tötet er keinen seiner Gegner.', 1, '1.500.000.000 Berry', '19', 'Strohhut-Bande', 'keine', 'Kapitän', 3, 3),
(13, '63d5b1122a6c59.01389344', 'Absalom', 'アブサロム', 'onepiecevzabsalom', 'Willi Röbke', 'Hiroaki Miura', 'Mensch', '30. Dezember', 'Westblue', 'Männlich', '1,95m', 'Unbekannt', 'Episode 339', 'Band 46, Kapitel 444', '', 0, 'Unbekannt', '36 (Tod)', 'Morias Piratenbande', 'Unsichtbarkeits-Frucht / 	Suke Suke no Mi', 'Kommandant', 0, 0),
(14, '63d5b1122aa008.17512714', 'Aissa', 'アイサ', 'onepieceverzeichnisaissa', 'Sabine Bohlmann', 'Masami Suzuki', 'Mensch', '03. Januar', 'Shandora, Upper Yard', 'Weiblich', '1,40', 'Unbekannt', 'Episode 161', 'Band 27, Kapitel 249', 'Aissa gehört zum Volk der Shandia. \r\n\r\nGenau wie Enel und dessen Priester beherrscht sie das Mantra.', 0, 'Unbekannt', '11', 'Keine', 'Keine', 'Unbekannt', 0, 0),
(15, '63d5b1122b0708.73151065', 'Kuzan', 'クザン', 'onepieceverzeichniskuzan', 'Ole Pfennig', 'Takehito Koyasu', 'Mensch', '21. September', 'Southblue', 'Männlich', '2,98', '', 'Episode 225', 'Band 32, Kapitel 303', 'Kuzan besaß bis zum Austritt der Marine den Titel des Admirals zusammen mit [ref=Sakazuki]Akainu[/ref] und [ref=Borsalino]Kizaru[/ref].\r\n\r\nDer Name als Admiral war auch \"Aokiji, der Blaue Fasan!\"\r\n\r\nNach der Schlacht auf dem Marine Ford wurde er von Senghok als Nachfolger vorgeschlagen. Für diese Entscheidung lieferte sich ein Kampf mit Sakazuk, welchen er verlor. Der Kampf ging 10 Tage.\r\n\r\nKuzan verlas danach die Marine und befindet sich seitdem im Untergrund der neuen Welt.\r\n\r\n', 0, '', '49', '', 'Gefrier-Frucht', 'Ehemaliger Admiral', 0, 0),
(16, '63d5b1122b4194.81895772', 'Sakazuki', 'サカズキ', 'onepieceverzeichnisakainu', 'Gerhard Acktun,  Michael Brennicke, Holger Schwiers', 'Fumihiko Tachiki, Michio Nakao', 'Mensch', '16. August', 'Northblue', 'Männlich', '3,06m', 'Unbekannt', 'Episode 278', 'Band 41, Kapitel 397', 'Sakazuki ist Großadmiral der Marine. Bis vor 2 Jahren besaß er noch den Titel des Admirals, zusammen mit [ref=Kuzan]Kuzan[/ref] und [ref=Borsalino]Kizaru[/ref].\r\n\r\nIhm begleitet als Admiral auch der Name Akainu \"Roter Hund\".\r\n\r\nSakazuki ist für den Tod von Puma D. Ace verantwortlich.\r\n\r\nNachdem Krieg im Marine Ford führte einen Kampf mit [ref=Kuzan]Kuzan[/ref] um den Titel des Großadmirals, dieser Kampf dauerte 10 Tage. Sakazuki gewann diesen Kampf, danach wurde er zum Großadmiral ernannt.\r\n\r\n[b]Sakazuki[/b] war es welcher das Marine Hauptquartier in die neue Welt verlagerte.', 0, '/', '55', 'Marine Hauptquartier', 'Magma Frucht / Magu Magu no Mi', 'Großadmiral', 0, 0),
